def approve(token,spender,amount,gasPrice,account,infura_url,key,abi) :
    #token = '0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2' token to approve
    #spender = '0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D' spender's address
    #amount = 10000000000 amount to approve
    #account = '0x830BBe006C2Ed0a4c815C9dBd193515e1c4B06xx' address of your wallet that holds the tokens to approve and will pay for the transaction
    #key = '0x117065c110de3111a72d1105asd33603a12345a951cd8346xxx27615f7221f11' your wallet's private key
    #infura_url = 'https://mainnet.infura.io/v3/11fb11111aa11d111f7b11c0c11ab111'
    #abi = '[{"constant":true,"inputs":[],"name":"name","outputs":[{"name":"","type":"string"}]..."}]' token contract (json) abi fetched from etherscan
    from web3 import Web3
    import json
    web3 = Web3(Web3.HTTPProvider(infura_url))

    weth_abi = json.loads(abi)
    contract_address = token
    contract = web3.eth.contract(address=contract_address, abi=weth_abi)
    fun = contract.functions.approve(spender,int(amount))
    tx = fun.buildTransaction({'from': account,'nonce': web3.eth.getTransactionCount(account),'gasPrice': Web3.toWei(int(gasPrice), 'gwei'),})
    signed_tx = web3.eth.account.signTransaction(tx, key)
    emitted = web3.eth.send_raw_transaction(signed_tx.rawTransaction)
    return emitted

